CREATE TABLE ADDRESS (member_user_id INTEGER, house_number TEXT, street TEXT, town TEXT);
CREATE TABLE USER (user_id INTEGER, email TEXT, given_name TEXT, surname TEXT, city TEXT, phone_number TEXT, profile_description TEXT, password TEXT);
CREATE TABLE CAREGIVER (caregiver_user_id INTEGER, photo TEXT, gender TEXT, caregiving_type TEXT, hourly_rate REAL);
CREATE TABLE MEMBER (member_user_id INTEGER, house_rules TEXT, dependent_description TEXT);
CREATE TABLE JOB (job_id INTEGER, member_user_id INTEGER, required_caregiving_type TEXT, other_requirements TEXT, date_posted TEXT);
CREATE TABLE APPOINTMENT (appointment_id INTEGER, caregiver_user_id INTEGER, member_user_id INTEGER, appointment_date TEXT, appointment_time TEXT, work_hours REAL, status TEXT);
CREATE TABLE JOB_APPLICATION (caregiver_user_id INTEGER, job_id INTEGER, date_applied TEXT);
